import React from 'react';
import './ButtonAdd.css';
import { useDispatch ,useSelector} from "react-redux";
import { setItemInCart, deleteItemFromCart} from '../../../redux/cart/reducer'
import { QuantityPicker } from 'react-qty-picker';

const ButtonAdd = ({item, quantity}) => {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.cart.itemsInCart);
  const isItemInCart = products.some((product) => product.id === item.id);


  const handleClick = (e) => {
    e.stopPropagation();
    if (isItemInCart) {
      dispatch(deleteItemFromCart(item.id));
    } else {
      dispatch(setItemInCart(item));
    }
  };

  return (
  <>

    <div>
        <span className='foot_desc-price'>${item.price}</span>
    </div>

    <div className='btnAddRemove' onClick={handleClick}>
      {quantity !== 0 ? (
        <div className='btnAddRemove-positive'>
          <QuantityPicker min={1} aria-hidden='true'/>
          <span>{quantity}</span>
          <QuantityPicker max={30} aria-hidden='true'/>
        </div>
      ) : (
      
           isItemInCart ? "Remove" : "Add"
        
      )} 
    </div>
</>
   
  );

};

export default ButtonAdd;
