import React from 'react';
import './Menu.css';
import MenuItem from './MenuItem/MenuItem';
import {ascending} from "./data"
import { useState } from 'react';

const Menu = ({list}) => {

  const [searchField, setSearchField] = useState("");

  
  const filteredPersons = list.filter(
    person => {
      return (
        person
        .name
        .toLowerCase()
        .includes(searchField.toLowerCase())
      );
    }
  );


  const asc = list.sort((a,b)=>a.name.localeCompare(b.name))
  const desc = list.sort((a,b)=>b.name.localeCompare(a.name))

  const ascPrice = list.sort((a,b)=>parseFloat(a.price) - parseFloat(b.price))
  const descPrice = list.sort((a,b)=>parseFloat(b.price) - parseFloat(a.price))

  console.log(ascending.asc)


  const handleChange = e => {
    setSearchField(e.target.value);
  };

  
  return(
    <div>
      <center><input 
          className="search__input"
          type="text" 
          placeholder="Search" 
          onChange = {handleChange}
        /></center>
    
   
  <main>
    {filteredPersons.map((item) => (
      <MenuItem item={item} key={item.id}/>
    ))}
  </main>
  </div>
  )
};

export default Menu;
