import React from "react";
import "./MenuItem.css";
import ButtonAdd from "./../../ButtonAdd/ButtonAdd";
import { useNavigate } from "react-router";
import { useDispatch } from "react-redux";
import { setCurrentItem } from "../../../../redux/item/reducer";
import {cartAddItem,cartRemoveItem,} from '../../../../redux/cart/cart.actions';


const MenuItem = ({ item,cartCount,cartList,cartAddItem,cartRemoveItem, }) => {

  const { name, description, image, rating, price } = item;
  const history = useNavigate();
  const dispatch = useDispatch();

  const handleClick = () => {
    dispatch(setCurrentItem(item));
    history(`/app/${item.name}`);
  };


  const handleQuantity = () => {
    let quantity = 0;
    if (cartCount !== 0) {
      const foundItemInCart = cartList.find((product) => product.id === item.id);
      if (foundItemInCart) {
        quantity = foundItemInCart.quantity;
      }
    }
    return quantity;
  };



  return (
    <div className="item" onClick={handleClick}>
      <img src={image} alt="item" />
      <div className="item-head_desc">
        <p className="head_desc-name">{name}</p>
      </div>
      <div className="item-foot_desc">
        {/* <span className='foot_desc-price'>${price}</span> */}
        <ButtonAdd 
        item={item} 
        quantity={handleQuantity} 
        handleRemoveItem={() => cartRemoveItem(item)}
        handleAddItem={() => cartAddItem(item)}/>
      </div>
    </div>
  );
};


const mapDispatchToProps = (dispatch) => ({
  cartAddItem: (item) => dispatch(cartAddItem(item)),
  cartRemoveItem: (item) => dispatch(cartRemoveItem(item)),
});

export default connect(mapDispatchToProps)(MenuItem);
