import React, { forwardRef } from 'react';
import './Menu.css';
import MenuItem from './MenuItem/MenuItem';
import { useState } from 'react';



const Menu = forwardRef(({ list }, ref) => {

  const [searchField, setSearchField] = useState("");
  const [sortField, sortsetField] = useState("");


  
  const filteredPersons = list.filter(
    person => {
      return (
        person
        .name
        .toLowerCase()
        .includes(searchField.toLowerCase())
      );
    }
  );



  
    const asc = list.sort((a,b)=>a.name.localeCompare(b.name))
    const desc = list.sort((a,b)=>b.name.localeCompare(a.name))
    const ascPrice = list.sort((a,b)=>parseFloat(a.price) - parseFloat(b.price))
    const descPrice = list.sort((a,b)=>parseFloat(b.price) - parseFloat(a.price))




  const handleChange = e => {
    setSearchField(e.target.value);
  };



  // const Change = e => {
  //   asc();
  // }


  return(
    <div>

      <button className="" onClick={asc}>Asc</button>
      <button className="" onClick={desc}>desc</button>
      <button className="" onClick={ascPrice}>ascPrice</button>
      <button className="" onClick={descPrice}>descPrice</button>
      
      <center><input 
          className="search__input"
          type="text" 
          placeholder="Search" 
          onChange = {handleChange}
        /></center>
    
   
  <main ref={ref}>
    {filteredPersons.map((item) => (
      <MenuItem item={item} key={item.id}/>
    ))}
  </main>
  </div>
  )

    })

export default Menu;
