import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { setItemInCart, deleteItemFromCart} from '../../../redux/cart/reducer'
import { AiOutlineCloseCircle, AiOutlineMinusCircle, AiOutlinePlusCircle } from "react-icons/ai";
import "./CartItem.css";


export const CartItem = ({ title, price, name, items }) => {
  const dispatch = useDispatch();
  const [newPrice, setNewPrice] = useState(price);

  const handleDeleteClick = () => {
    dispatch(deleteItemFromCart(name));
  };

  return (
    <div className="cart-item">
      <span>{title} </span>
      <div className="cart-item__price">

        <AiOutlinePlusCircle className="plus" onClick={() => {
          console.log('price', price);
          setNewPrice(newPrice+price);
        }}/>
        <AiOutlineMinusCircle className="minus" />

        <span>{newPrice} $</span>
        <AiOutlineCloseCircle
          size={15}
          className="cart-item__delete-icon"
          onClick={handleDeleteClick}
        />

        {items.length > 0 ? (
        <div className="cart-menu__arrange">
          <div className="cart-menu__total-price">
            <span>Overral:</span>
            <span>{totalPrice.toFixed(2)} $</span>
          </div>
          <Button type="primary" size="s" onClick={onClick}>
             Checkout 
          </Button>
        </div>
      ) : null}
      </div>
    </div>
  );
};
