import React from 'react';
import './ButtonAdd.css';

const ButtonAdd = ({item, quantity, handleAddItem, handleRemoveItem }) => {


  const handleClick = (e) => {
    e.stopPropagation();
  }


  return (
  <>

    <div>
        <span className='foot_desc-price'>${item.price}</span>
    </div>

    <div className='btnAddRemove'>
      {/* {quantity !== 0 ? (
        <div className='btnAddRemove-positive'>
          <i
            class='fa fa-minus'
            aria-hidden='true'
            onClick={handleRemoveItem}
          ></i>
          <span> {quantity}</span>
          <i class='fa fa-plus' aria-hidden='true' onClick={handleAddItem}></i>
        </div>
      ) : ( */}
     
        
        <div onClick={handleClick} className='btnAddRemove-negative'>
          <span>ADD </span>
          <i class='fa fa-plus' aria-hidden='true'></i>
        </div>
        
        
      {/* )} */}
    </div>
</>
   
  );
};

export default ButtonAdd;
