import './ButtonAdd.css';

const ButtonAdd = ({price, quantity, handleAddItem, handleRemoveItem }) => {
  return (
  
    <div className='btnAddRemove'>
      {/* {quantity !== 0 ? (
        <div className='btnAddRemove-positive'>
          <i
            class='fa fa-minus'
            aria-hidden='true'
            onClick={handleRemoveItem}
          ></i>
          <span> {quantity}</span>
          <i class='fa fa-plus' aria-hidden='true' onClick={handleAddItem}></i>
        </div>
      ) : ( */}
     
        
        <div onClick={handleAddItem} className='btnAddRemove-negative'>
          <span>ADD </span>
          <i class='fa fa-plus' aria-hidden='true'></i>
        </div>
        <span className='foot_desc-price'>{price}</span>
        
      {/* )} */}
    </div>
   
  );
};

export default ButtonAdd;
