import React, { forwardRef } from 'react';
import './Menu.css';
import MenuItem from './MenuItem/MenuItem';
import { useState } from 'react';



const Menu = forwardRef(({ list }, ref) => {

  const [searchField, setSearchField] = useState("");
  
  const [sortAscField, setSortAscField] = useState("");
  const [sortDescField, setSortDescField] = useState("");
  const [sortPriceAscField, setSortPriceAscField] = useState("");
  const [sortPriceDescField, setSortPriceDescField] = useState("");


  
  const filteredPersons = list.filter(
    person => {
      return (
        person
        .name
        .toLowerCase()
        .includes(searchField.toLowerCase())
      );
    }
  );



  
    const asc = filteredPersons.sort((a,b)=>a.name.localeCompare(b.name))
    // const desc = asc.sort((a,b)=>b.name.localeCompare(a.name))
    // const ascPrice = desc.sort((a,b)=>parseFloat(a.price) - parseFloat(b.price))
    // const descPrice = ascPrice.sort((a,b)=>parseFloat(b.price) - parseFloat(a.price))




  const handleChange = e => {
    setSearchField(e.target.value);
  };


  const ascChange = e => {
    setSortAscField(e.target.value)
  }

  const descChange = e => {
    setSortDescField(e.target.value)
  }

  const ascPrices = e => {
    setSortPriceAscField(e.target.value)
  }

  const descPrices = e => {
    setSortPriceDescField(e.target.value)
  }




  return(
    <div>

      <button className="" onClick={ascChange}>Asc</button>
      <button className="" onClick={descChange}>desc</button>
      <button className="" onClick={ascPrices}>ascPrice</button>
      <button className="" onClick={descPrices}>descPrice</button>
      
      <center><input 
          className="search__input"
          type="text" 
          placeholder="Search" 
          onChange = {handleChange}
        /></center>
    
   
  <main ref={ref}>
    {asc.map((item) => (
      <MenuItem item={item} key={item.id}/>
    ))}
  </main>
  </div>
  )

    })

export default Menu;
