import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setItemInCart, deleteItemFromCart} from '../../../redux/cart/reducer'
import { AiOutlineCloseCircle } from "react-icons/ai";
import "./CartItem.css";
import { AiOutlinePlusCircle } from "react-icons/ai";
import { AiOutlineMinusCircle } from "react-icons/ai";

export const CartItem = ({ title, price, name }) => {
  const dispatch = useDispatch();
  
  const [counter, setCounter] = useState(1);
  let incrementCounter = () => setCounter(counter + price);
  let decrementCounter = () => setCounter(counter - price);

  if(counter<=0) {
    decrementCounter = () => setCounter(1);
  }


  const handleDeleteClick = () => {
    dispatch(deleteItemFromCart(name));
  };



  return (
    <div className="cart-item">
      <span>{title} </span>
      <div className="cart-item__price">

        <AiOutlinePlusCircle className="plus" onClick={incrementCounter}/>
        <AiOutlineMinusCircle className="minus" onClick={decrementCounter} />

        <span value={counter}>{price} $</span>
        <AiOutlineCloseCircle
          size={15}
          className="cart-item__delete-icon"
          onClick={handleDeleteClick}
        />
      </div>
    </div>
  );
};
