import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { setItemInCart, deleteItemFromCart} from '../../../redux/cart/reducer'
import { AiOutlineCloseCircle } from "react-icons/ai";
import "./CartItem.css";
import { AiOutlinePlusCircle } from "react-icons/ai";
import { AiOutlineMinusCircle } from "react-icons/ai";

export const CartItem = ({ title, price, name }) => {
  const dispatch = useDispatch();
  const item = useSelector(state => state.item.currentItem);
  console.log(item)

  const handleDeleteClick = () => {
    dispatch(deleteItemFromCart(name));
  };

  return (
    <div className="cart-item">
      <span>{title} </span>
      <div className="cart-item__price">
        {/* <AiOutlinePlusCircle className="plus" onClick={}/>
        <AiOutlineMinusCircle className="minus" onClick={}/> */}
        <span>{price} $</span>
        <AiOutlineCloseCircle
          size={15}
          className="cart-item__delete-icon"
          onClick={handleDeleteClick}
        />
      </div>
    </div>
  );
};
